let num1 = document.getElementById("num1") as HTMLInputElement;
let num2 = document.getElementById("num2") as HTMLInputElement;

let addBtn = document.getElementById("addBtn") as HTMLElement;

const addition = (n1:number, n2:number, showTesult : boolean)  => {
    if(showTesult){
        return `${num1} + ${num2}`
    }
    else{
        console.log(n1+n2);
    }
}

addBtn.addEventListener("click", () => {
    addition(+num1.value, +num2.value, true)
})